import React from 'react';

function Search () {
    return (
        <form>
            <label></label>
            <input
                value = {props.search}
                name = "employeeSearch"
                type = "text"
                placeholder = "Enter Employee Name"
                id = "search"
                onChange = {props.inputChange}
            />
            <submit onClick = {props.submitForm}></submit>
        </form>
    )
}


export default Search;
