import React, { useEffect, useState, Component } from "react";
import API from "../utils/API";
import Search from "./searchForm";
import Employeetable from "./employeeTable";

class EmployeeData extends Component {
  state = {
    employees: [{}],
    filtered: [{}],
    order: 'descend'
  };

  componentDidMount() {
    API.getUsers()
      .then((res) =>
        this.setState({
          employees: res.data.results,
          filtered: res.data.results,
        })
      )
      .catch((err) => console.log(err));
  }

  handleInputChange = event => {
    const employeeSearch = event.target.value;
    const Searched = this.state.employees.map( employee => employee.name.first.toLowerCase().indexOf(employeeSearch.toLowerCase().trim()));
    this.setState({filtered: Searched});
  }

  render() {
    return (
      <div>
        <Search handleInputChange={this.handleInputChange} />
        {/* <table> */}
          <Employeetable 
            employees = {this.state.filtered} />
        {/* </table> */}
      </div>
    );
  }
}

export default EmployeeData;
